// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;
pragma experimental ABIEncoderV2;

// On REMIX, use CRTL+SHIFT+S to run @custom:dev-run-script
/**
 * @title Monitoring Data Notarization
 * @author Alessandro Rossi (ENG), 2023
 * @dev Notarization of monitoring data into the Blockchain through the hash of their dump at each timeslot.
 */
contract MonitoringDataNotarization {

    struct Measurement {
        string label; // unencrypted data attached to the dump (e.g., the query to retrieve it) 
        bytes32 hash; // encrypted dump of the monitoring data (saved in the off-chain storage)
    }

    mapping (address => mapping (uint => Measurement)) private measurements; // contract status
    //i.e., (data provider, timestamp)->{label, hash}

    uint16 private timeslot; // duration in seconds of each slot into which the dump is divided
    address private owner; // the creator, administrator and maintainer of the present contract

    event Notarized(uint timestamp, Measurement measurement);

    constructor(uint16 timeslotInSeconds) {
        owner = msg.sender;
        timeslot = timeslotInSeconds; // e.g., 900 = 15 minutes
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner authorized");
        _;
    }

     //=================//
    // ADMIN FUNCTIONS //
   //=================//

    /// @dev Force the notarization of a monitoring data hash for a given data provider at a given unchecked timestamp
    function addMeasurement(address provider, uint timestamp, string calldata label, bytes32 hash) 
            external onlyOwner returns (uint timestampInSeconds, Measurement memory measurement) {
        measurements[provider][timestamp] = Measurement(label, hash);

        emit Notarized(timestamp, measurements[provider][timestamp]);
        return (timestamp, measurements[provider][timestamp]); // works only if called by a contract, use events externally
    }

    /// @dev Get the Measurement notarized for a given data provider at a given timestamp
    function getMeasurement(address provider, uint timestamp)
            external onlyOwner view returns (Measurement memory measurement) { 
        return measurements[provider][timestamp];
    }

     //==================//
    // HELPER FUNCTIONS //
   //==================//

    /// @dev Calculate the monitoring data hash with Keccak 256 (SHA-3) algorithm
    function encodeHash(string calldata data)
            public pure returns (bytes32 measurementHash) {
        return keccak256(abi.encodePacked(data));
    }

    /// @dev Get the metadata attached to the monitoring data notarized at a given timestamp
    function getLabel(uint timestamp) 
            external view returns (uint normalizedTimestamp, string memory measurementLabel) {
        timestamp = normalize(timestamp);
        require(measurements[msg.sender][timestamp].hash != "", "No data notarized");

        return (timestamp, measurements[msg.sender][timestamp].label);
    }

    /// @dev Get the established timeslot duration
    function getTimeslotDuration() 
            external view returns (uint16 timeslotInSeconds) {
        return timeslot;
    }

    /// @dev Get the current timestamp
    function getTimestamp()
            public view returns (uint timestampInSeconds) {
        return block.timestamp;
    }

    /// @dev Set the timestamp to the preceding timeslot (e.g., 01:23:45 --> 01:15:00, if timeslot=15 min) 
    function normalize(uint timestamp)
            public view returns (uint normalizedTimestamp) {
        return timestamp / timeslot * timeslot;
    }

     //========================//
    // NOTARIZATION FUNCTIONS //
   //========================//

    /// @dev Calculate and notarize the hash of monitoring data, normalizing the current timestamp
    function certify(string calldata label, string calldata data) 
            external returns (uint timestampInSeconds, Measurement memory measurement) {
        return notarize(normalize(getTimestamp()), label, encodeHash(data));
    }

    /// @dev Register a hash into the blockchain. at a given normalized unused timestamp
    function notarize(uint timestamp, string calldata label, bytes32 hash) 
            public returns (uint timestampInSeconds, Measurement memory measurement) {
        require(hash != "", "Hash cannot be null");
        require(timestamp > 0 && timestamp % timeslot == 0, "Timestamp must be normalized");
        require(measurements[msg.sender][timestamp].hash == "", "Data already notarized");

        measurements[msg.sender][timestamp] = Measurement(label, hash);

        emit Notarized(timestamp, measurements[msg.sender][timestamp]);
        return (timestamp, measurements[msg.sender][timestamp]); // works only if called internally, use events externally
    }

     //========================//
    // VERIFICATION FUNCTIONS //
   //========================//

    /// @dev Calculate and verify if a monitoring data hash has been notarized, normalizing the given timestamp
    function check(uint timestamp, string calldata data)
            external view returns (bool isNotarized) {
        return verify(normalize(timestamp), encodeHash(data));
    }

    /// @dev Prove if a monitoring data hash has been notarized at a given used timestamp
    function verify(uint timestamp, bytes32 hash)
            public view returns (bool isNotarized) {
        require(hash != "", "Hash cannot be null");
        require(timestamp > 0 && timestamp % timeslot == 0, "Timestamp must be normalized");
        require(measurements[msg.sender][timestamp].hash != "", "No data notarized");

        return measurements[msg.sender][timestamp].hash == hash;
    }
}

